    `Média` = ~mean(., na.rm = TRUE)
   #,
   # s.d. = ~sd(., na.rm = TRUE)
  )

penguins_gt <- 
  penguins_gt %>% 
  summary_rows(
    groups = TRUE,
    columns = c(`Adelie (female)`, `Adelie (male)`,
                `Gentoo (female)`, `Gentoo (male)`,
                `Chinstrap (female)`, `Chinstrap (male)`),
    fns = fns_labels,
    formatter = fmt_number,
    decimals = 0
    ) %>% 
