  gt(rowname_col = "stream") |> 
  tab_header(
    title = md(paste("BID Quantities", fontawesome::fa("city") , "FMS-SAP")),
    subtitle = "Status at 2023-12-19"
  ) |>
  sub_missing()  |> 
  grand_summary_rows(
